import React from "react"

class Project extends React.Component {
    render() {
        let url

        if(this.props.lang == 0){
            image = "url(https://icons.iconarchive.com/icons/papirus-team/papirus-apps/512/python-icon.png) center / cover"
        }

        return(
            <Card shadow={5} style={{minWidth: "450", margin: "auto", marginRight: "40px"}}>
                <CardTitle style={{color: "black", height: "190px", background: image}}>
                    {this.props.projectTitle}
                </CardTitle>
                <CardText>
                    {this.props.projectDescription}
                </CardText>
                <CardActions border>
                    <Button colored>GitHub</Button>
                </CardActions>
                <CardMenu style={{color: "#fff"}}>
                    <IconButton name="share" />
                </CardMenu>
            </Card>
        )
    }
}

export default Project
