import React from "react"
import { Tabs, Tab, Grid, Cell } from "react-mdl"

import Project from "./project"

class Projects extends React.Component {
    constructor() {
        super()

        this.state = {
            activeTab: 0
        }
    }
    
    toggleCategories() {
        if(this.state.activeTab === 0){
            return(
                <div className="projects-grid">
                    {/* <Card shadow={5} style={{minWidth: "450", margin: "auto", marginRight: "40px"}}>
                        <CardTitle style={{color: "black", height: "190px", background: "url(https://icons.iconarchive.com/icons/papirus-team/papirus-apps/512/python-icon.png) center / cover"}}>
                            Python Project 1
                        </CardTitle>
                        <CardText>
                            Description of project
                        </CardText>
                        <CardActions border>
                            <Button colored>GitHub</Button>
                        </CardActions>
                        <CardMenu style={{color: "#fff"}}>
                            <IconButton name="share" />
                        </CardMenu>
                    </Card>

                    <Card shadow={5} style={{minWidth: "450", margin: "auto"}}>
                        <CardTitle style={{color: "black", height: "190px", background: "url(https://icons.iconarchive.com/icons/papirus-team/papirus-apps/512/python-icon.png) center / cover"}}>
                            Python Project 2
                        </CardTitle>
                        <CardText>
                            Description of project
                        </CardText>
                        <CardActions border>
                            <Button colored>GitHub</Button>
                        </CardActions>
                        <CardMenu style={{color: "#fff"}}>
                            <IconButton name="share" />
                        </CardMenu>
                    </Card> */}

                    <Project 
                        projectName = "New Project"
                        projectDescription = "This is a test of a new component"
                        lang = {this.state.activeTab}
                    />
                    <Project 
                        projectName = "New Project"
                        projectDescription = "This is a test of a new component"
                        lang = {this.state.activeTab}
                    />
                    <Project 
                        projectName = "New Project"
                        projectDescription = "This is a test of a new component"
                        lang = {this.state.activeTab}
                    />
                </div>
            )
        }
        else if(this.state.activeTab === 1){
            return(
                <div><h1>This is Rails</h1></div>
            )
        }
        else if(this.state.activeTab === 2){
            return(
                <div><h1>This is React</h1></div>
            )
        }
    }

    render() {
        return (
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>Python</Tab>
                    <Tab>Rails</Tab>
                    <Tab>React</Tab>
                </Tabs>

                <Grid>
                    <Cell col={12}>
                        <div className="content">
                            {this.toggleCategories()}
                        </div>
                    </Cell>
                </Grid>
            </div>
        )
    }
}
 
export default Projects
